import React, { useEffect } from 'react'
import { TextInput,View ,Text,Button } from 'react-native';
import {HomeIcon} from "react-native-heroicons/solid";
import OrderScreen from '../screen/OrderScreen';

const Header = ({navigation}) => {

      // const printer = () =>{
      //        alert("printer");
      //        }
      // useEffect(()=>{
      //        printer();
      // },[])


  return (
      <View className="w-full h-2/12 fixed flex flex-row justify-evenly items-center bg-slate-100 mt-6 top-0 z-50" >
                <View className="w-full h-full py-2 flex flex-row justify-evenly items-center bg-slate-900" >
                <Text className="h-2/12 flex justify-content-center align-items-center text-yellow-500" >
                     Logo
                </Text>
                 <TextInput
                     className="w-8/12 rounded-0 px-2 py-1 text-yellow-300 bg-transparent border-2 border-yellow-500"
                     placeholderTextColor="#eda909"
                     placeholder="Search Dishes...."
                  >
                 </TextInput>
                 <Button 
                    title='Cart'
                    color='#e89b0c'
                  //   onPress={printer()}
                  >
                 </Button>
                </View>
      </View>
  )
}

export default Header;
