import React,{ useLayoutEffect } from 'react';
import { View , Text, SafeAreaView , ScrollView, Pressable} from 'react-native';
import { useNavigation } from '@react-navigation/native';

import Header from '../component/Header';
import Footer from '../component/Footer';

const UserScreen = () => {

  const navigation = useNavigation();

  //  removing default navigation bar
  useLayoutEffect(()=>{
    navigation.setOptions({
      headerShown: false,
    });
  }, []);
  
  return (
    <SafeAreaView className="w-full h-full flex flex-column" >
       <Header className="top-0 right-0 left-0 absolute z-40" />

        {/* user cont begins */}
         <ScrollView className="w-full h-11/12 flex flex-column" >
         <View className="w-full h-60 bg-gray-300 flex flex-row justify-center items-center" >
              <View className="w-1/2 h-full flex flex-col justify-center items-center" >
                  <Pressable className="bg-blue-500 w-20 h-10 text-center flex justify-center items-center" onPress={(e)=>{
                     navigation.navigate("SignUp")
                  }} >
                         <Text>
                             SignUp
                         </Text>
                  </Pressable>
              </View>
              <View className="w-1/2 h-full flex flex-col justify-center items-center" >
                  <Pressable className="bg-red-500 w-20 h-10 text-center flex justify-center items-center " onPress={(e)=>{
                     navigation.navigate("Login")
                  }} >
                         <Text>
                             Login
                         </Text>
                  </Pressable>
              </View>
         </View>
         </ScrollView>
       <Footer className="right-0 left-0 bottom-0 absolute z-40" />

    </SafeAreaView>
  );
}

export default UserScreen;
