import React,{useEffect, useState} from 'react';
import { useNavigation } from '@react-navigation/native';
import { View , Text , Image , TouchableNativeFeedback ,Pressable } from 'react-native';
import { BellAlertIcon , ClockIcon  , ShieldExclamationIcon , Cog6ToothIcon , ChatBubbleBottomCenterIcon , XMarkIcon} from "react-native-heroicons/solid";

const ToggleMenu = (props) => {

  const navigation = useNavigation();

  const [route, setRoute] = useState("");
  console.log(route)
  const liElement = [{
                       id : 1,
                       icon : <BellAlertIcon />,
                       text : "Notification",
                       route : "Notification",
                  
                      },
                      {
                       id : 2,
                       icon : <ClockIcon />,
                       text : "History",
                       route : "Home",

                      },
                      {
                       id : 3,
                       icon : <ChatBubbleBottomCenterIcon />,
                       text : "Chat",
                       route : "Chat",

                      },
                      {
                       id : 4,
                       icon : <ShieldExclamationIcon />,
                       text : "Terms and Condition",
                       route : "TermsAndCondition",

                      },
                      {
                        id : 5,
                        icon : <Cog6ToothIcon />,
                        text : "Setting",
                        route : "Setting",
                       }
]
  const dismissMenu = (e) =>{
      props.parentCallBack(true);
      e.preventDefault();
  }
  // const navigatorFun = ()=>{
  //     if(route){
  //       console.log(route);
  //       props.navigation.navigate(route);
  //     }   
  // }
  // navigatorFun();

  return (
    <View className="w-3/4 h-11/12 mt-6 bg-white left-0 top-0 bottom-0 absolute z-50">
        <View className="w-full h-1/5 flex flex-row justify-center items-center bg-slate-900" >
             <View className="w-3/4 px-2 h-full flex flex-row justify-start items-center">
               <View className="flex justify-center items-center rounded-full">
                    <Image className="w-20 h-20 rounded-full" source={{ 
                      uri : "https://www.simplyrecipes.com/thmb/lapjIm2IrSHGS8ptjyhspa_BboU=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/Simply-Recipes-Tandoori-Chicken-LEAD-01-7767f2892b824b24b65213f3ba28ff8c.jpg",
                    }} />
                    <View className="w-full px-2 text-xl flex flex-col">
                         <Text className="text-md text-white" >
                                  UserName 
                         </Text>
                         <Text className="text-xsm text-gray-500" >
                                  userEmail
                        </Text>
                    </View>
               </View>
             </View>
             <View className="w-1/4 h-full py-3 d-flex justify-start items-center" >
                 <Text onPress={(e)=>{
                   dismissMenu(e)
                 }} >
                 <XMarkIcon color="#fff" />
                 </Text>
             </View>
        </View>
        <View className="w-full my-2 flex flex-column justify-center items-center" >
             {liElement.map((d)=>{
                  return(
                     <Pressable className='bg-red-500 w-full py-2 my-1' key={d.id} onPress={()=>{alert(d.route);navigation.navigate(d.route)}}>
                          <View className="w-11/12 flex flex-row justify-start items-center" >
                                <Text>
                                  {d.icon}
                                </Text>
                                <Text className="px-2" >
                                   {d.text}
                                </Text> 
                          </View>   
                     </Pressable>
                  )
             })}
        </View>
    </View>
  )
}

export default ToggleMenu;
