import { View, Text, TextInput, Image, ScrollView , SafeAreaView } from "react-native";
import React, { useLayoutEffect, useState } from "react";
import { useNavigation } from "@react-navigation/native";
import {FaMapMarkerAlt,FaStream} from "react-icons/fa";
import { firebase } from '@react-native-firebase/database';
import dish from "../data/dish.json";
import Header from '../component/Header';
import Footer from '../component/Footer';
import DishCard from "../component/DishCard";


const HomeScreen = () => {

  const navigation = useNavigation();
  const [text, setText] = useState('');

  useLayoutEffect(() => {
    navigation.setOptions({
      headerShown: false,
    });
  }, []);
  
  const handleSearchBar = (e) =>{
    setText(e.target.value);
  }
   
  return (

    <SafeAreaView className="min-w-screen min-h-screen flex flex-col justify-between items-center">
      <Header />
      <ScrollView className="w-full bg-slate-900 h-full flex flex-column overflow-y-scroll">
          <View className="w-full h-full flex flex-column" >
          {dish.map((d,index)=>{
              return <DishCard key={index} originalURL = {d.originalURL} image={d.imageURL} name={d.name} ingredients={d.ingredients} steps={d.steps} />
          })}
            <View className="bg-indigo-400" >
                <Text>
                Lorem ipsum dolor sit amet consectetur adipisicing elit. Eaque fuga, tenetur corrupti vitae officia cumque, hic laborum odio ea sapiente illum? Totam rem commodi explicabo perferendis soluta voluptatem adipisci voluptate, molestiae aliquam tenetur dolorem autem suscipit iure eum eligendi reiciendis sed minus. Debitis eaque similique ab delectus mollitia optio sit officia voluptatum repudiandae facere quas, animi, eum nisi veritatis deleniti natus, voluptates maxime rerum? Vitae, facilis voluptas?
                </Text>
            </View>
          </View>
      </ScrollView>
      <Footer />
    </SafeAreaView>
   
  );
};

export default HomeScreen;
