import React,{ useLayoutEffect } from 'react';
import { View , Text, SafeAreaView , ScrollView, Pressable} from 'react-native';
import { useNavigation } from '@react-navigation/native';

import Header from '../component/Header';
import Footer from '../component/Footer';

const UserScreen = () => {

  const navigation = useNavigation();

  //  removing default navigation bar
  useLayoutEffect(()=>{
    navigation.setOptions({
      headerShown: false,
    });
  }, []);
  
  return (
    <SafeAreaView className="w-full h-full flex flex-column" >
       <Header className="top-0 right-0 left-0 absolute z-40" />

        {/* user cont begins */}
         <ScrollView className="w-full" >
         <View className="w-full h-1/2 bg-red-300 flex flex-row justify-center items-center" >
              <View>
                  <Text>
                      SignUp
                  </Text>
                  <Pressable  onPress={(e)=>{
                     navigation.navigate("SignU")
                  }} ></Pressable>
              </View>
              <View>
                  <Text>
                      Login
                  </Text>
                  <Pressable  onPress={(e)=>{
                     navigation.navigate("Login")
                  }} ></Pressable>
              </View>
         </View>
         </ScrollView>
       <Footer className="right-0 left-0 bottom-0 absolute z-40" />

    </SafeAreaView>
  );
}

export default UserScreen
